'        || ''" />''',
'      ;',
'    else',
'      -- if p_app_page_id is not defined then generate',
'      -- robots no index meta tag',
'      apex_debug.warn( ''Canonical link tag not generated for tab.'');',
'      l_html := get_robots_noindex_meta;',
'    end if;',
'',
'    return l_html;',
'',
'  end get_tab_canonical_link;',
