'--------------------------------------------------------',
'--  DDL for View BLOG_V_TAGS',
'--------------------------------------------------------',
'CREATE OR REPLACE FORCE VIEW "BLOG_V_TAGS" ("TAG_ID", "TAG", "TAG_URL", "POSTS_COUNT", "CHANGED_ON", "TAG_BUCKET", "SHOW_POST_COUNT", "LIST_BADGE") AS',
