'',
'  -- Handle error cases',
'  exception',
'  when others',
'  then',
'',
'    apex_debug.error(',
'       p_message => ''Error: %s %s( %s => %s )''',
'      ,p0 => sqlerrm',
'      ,p1 => utl_call_stack.concatenate_subprogram(utl_call_stack.subprogram(1))',
'      ,p2 => ''p_attribute_name''',
