'from blog_categories t1',
'where 1 = 1',
'and t1.is_active = 1',
'and exists (',
'  select 1',
'  from blog_v_posts x1',
'  where 1 = 1',
'  and x1.category_id  = t1.id',
')',
'with read only',
'/',
