'from blog_posts t1',
'join blog_bloggers t2',
'  on t1.blogger_id  = t2.id',
'join blog_categories t3',
'  on t1.category_id = t3.id',
'join blog_post_uds t4',
'  on t1.id = t4.post_id',
'where 1 = 1',
'and t1.is_active = 1',
'and t2.is_active = 1',
'and t3.is_active = 1',
'and t1.published_on <= localtimestamp',
