'  function get_post_search(',
'    p_search in varchar2',
'  ) return varchar2',
'  as',
'    c_xml constant varchar2(32767)  := ''<query><textquery><progression>''',
'                                    ||    ''<seq>  #SEARCH#  </seq>''',
'                                    ||    ''<seq> ?#SEARCH#  </seq>''',
'                                    ||    ''<seq>  #SEARCH#% </seq>''',
'                                    ||    ''<seq> %#SEARCH#% </seq>''',
'                                    || ''</progression></textquery></query>'';',
'    l_search varchar2(32767) := p_search;',
