'      ,p1 => ''p_post_id''',
'      ,p2 => coalesce( p_post_id, ''(null)'' )',
'    );',
'',
'    -- show http error',
'    raise_http_error(',
'      p_error_code => 404',
'      ,p_message => ''BLOG_HTTP_404_ERROR''',
'    );',
'    raise;',
'',
'  when others',
'  then',
'',
'    apex_debug.error(',
'       p_message => ''Unhandled error. %s( %s => %s, %s => %s, %s => %s )''',
'      ,p0 => utl_call_stack.concatenate_subprogram(utl_call_stack.subprogram(1))',
'      ,p1 => ''p_post_id''',
'      ,p2 => coalesce( p_post_id, ''(null)'' )',
'      ,p3 => ''p_newer_id''',
'      ,p4 => p_newer_id',
'      ,p5 => ''p_older_id''',
'      ,p6 => p_older_id',
'    );',
'',
'    -- show http error',
'    raise_http_error(',
'      p_error_code => 404',
'      ,p_message => ''BLOG_HTTP_404_ERROR''',
'    );',
'    raise;',
'',
'  end get_post_pagination;',
