'    l_xml blob;',
'  begin',
'',
'    select xmlserialize( document',
'      xmlelement(',
'        "urlset",',
'        xmlattributes(''http://www.sitemaps.org/schemas/sitemap/0.9'' as "xmlns"),',
'        (',
'          xmlagg(',
'            xmlelement( "url"',
'              ,xmlelement( "loc", blog_url.get_archive(',
