'      ,p1 => ''p_post_id''',
'      ,p2 => coalesce( p_post_id, ''(null)'' )',
'    );',
'',
'    -- show http error',
'    raise_http_error( 404 );',
'    raise;',
'',
'  when others',
'  then',
'',
'    apex_debug.error(',
'       p_message => ''Unhandled error. %s( %s => %s )''',
