'--------------------------------------------------------------------------------',
'  function remove_whitespace(',
'    p_string  in varchar2',
'  ) return varchar2',
'  as',
'  begin',
'    -- remove whitespace characters from string',
'    return trim( regexp_replace( p_string, ''\s+'', '' '' ) );',
'  end remove_whitespace;',
