'      l_value := to_number( p_value );',
'      -- check value is integer and between range',
'      if round( l_value ) = l_value',
'      and l_value between p_min and p_max',
'      then',
'        -- if validation passes, clear error meassage',
'        l_err_mesg := null;',
'      end if;',
'    end if;',
'',
'    return l_err_mesg;',
'',
'  exception',
'  when invalid_number',
